mapper.select:
select m.MESSAGE_NUM, m.MESSAGE_ROOM, m.MESSAGE_SEND_ID, m.MESSAGE_RECV_ID, date_format(m.MESSAGE_SEND_TIME, '%Y-%m-%d %H:%i') AS MESSAGE_SEND_TIME, m.MESSAGE_READ_TIME, ifnull(m.MESSAGE_CONT,'') AS MESSAGE_CONT, m.MESSAGE_READ_CHK, (SELECT u.user_role FROM USERS u WHERE u.user_id = #{user_id}) AS USER_ROLE from message m left outer join users u ON m.MESSAGE_SEND_ID = u.USER_ID where 1 = 1 and
MESSAGE_NUM in (select max(MESSAGE_NUM) from message group by MESSAGE_ROOM) and (MESSAGE_SEND_ID = #{user_id} or MESSAGE_RECV_ID = #{user_id}) order by FIELD(MESSAGE_ROOM, #{message_room}, 0, 0) desc;
MESSAGE_NUM in (select max(MESSAGE_NUM) from message group by MESSAGE_ROOM) and (MESSAGE_SEND_ID = #{user_id} or MESSAGE_RECV_ID = #{user_id}) order by FIELD(MESSAGE_ROOM, #{message_room}, 0, 0) desc;
MESSAGE_NUM in (select max(MESSAGE_NUM) from message group by MESSAGE_ROOM) and (MESSAGE_SEND_ID = #{user_id} or MESSAGE_RECV_ID = #{user_id}) order by MESSAGE_NUM desc;

    select		if(count(*) > 0,
				tiper_img,
				'default.png') as tiper_img
	from 		tiper



            where 	TIPER_USER_ID = #{message_recv_id}


            where 	TIPER_USER_ID = #{message_send_id}

mapper.update:
update message set MESSAGE_READ_CHK = 1


        where 	MESSAGE_ROOM = #{message_room} and MESSAGE_READ_CHK = 0 and MESSAGE_RECV_ID = #{user_id}


        where 	MESSAGE_SEND_ID = #{message_recv_id} and MESSAGE_READ_CHK = 0 and MESSAGE_RECV_ID = #{user_id}
